import React, { useState } from "react";
import "./App.css";

function App() {
  const [isWalletConnected, setIsWalletConnected] = useState(false); // Wallet connection state
  const [showModal, setShowModal] = useState(false); // Modal visibility state
  const [nftType, setNftType] = useState(""); // NFT type for purchase

  // Connect to Phantom Wallet
  const connectWallet = async () => {
    if (!isPhantomInstalled()) {
      alert("Phantom Wallet is not installed. Please install it from https://phantom.app/");
      return;
    }

    try {
      const response = await window.solana.connect(); // Requests wallet connection
      const address = new PublicKey(response.publicKey.toString());
      setWalletConnected(true);
      setWalletAddress(address.toString());
      alert(`Wallet connected: ${address.toString()}`);
    } catch (err) {
      console.error("Error connecting to Phantom Wallet:", err);
      alert("Failed to connect wallet. Please try again.");
    }
  };

  // Open modal with NFT type
  const openModal = (type) => {
    if (!isWalletConnected) {
      alert("Please connect your wallet before making a purchase.");
      return;
    }
    setNftType(type);
    setShowModal(true);
  };

  // Close modal
  const closeModal = () => {
    setShowModal(false);
    setNftType("");
  };

  // Confirm purchase
  const confirmPurchase = () => {
    alert(`Purchase confirmed for ${nftType}!`);
    console.log(`Purchase confirmed for ${nftType}`);
    closeModal();
  };

  return (
    <div>
      <header>
        <div className="logo">
          <img
            src={`${process.env.PUBLIC_URL}/images/logoo.png`}
            alt="EcoNFT Energy Logo"
          />
        </div>
        <nav>
          <ul>
            <li><a href="#home">Home</a></li>
            <li><a href="#nfts">Buy NFTs</a></li>
            <li><a href="#passive-income">Passive Income</a></li>
          </ul>
        </nav>
        <button id="connect-wallet" onClick={connectWallet}>
          {isWalletConnected ? "Wallet Connected" : "Connect Wallet"}
        </button>
      </header>

      <section id="hero">
        <h1>Earn Passive Income with Renewable Energy</h1>
        <p>Buy NFTs representing solar panels and wind turbines to earn passive income!</p>
      </section>

      <section id="nfts">
        <h2>Our Energy NFTs</h2>
        <div className="background">
          <div className="left">
            <button
              className="buy-button"
              onClick={() => openModal("Solar Panel NFT")}
            >
              Buy Solar Panel NFT
            </button>
          </div>
          <div className="right">
            <button
              className="buy-button"
              onClick={() => openModal("Wind Turbine NFT")}
            >
              Buy Wind Turbine NFT
            </button>
          </div>
        </div>
      </section>

      <section id="passive-income">
        <h2>Passive Income Tracker</h2>
        <div className="income-tracker">
          <p>Your earnings from active NFTs:</p>
          <div id="income-value">0</div>
        </div>
      </section>

      <footer>
        <p>&copy; 2024 EcoNFT Energy. All rights reserved.</p>
      </footer>

      {/* Modal */}
      {showModal && (
        <div id="purchase-modal" className="modal" onClick={(e) => e.target === e.currentTarget && closeModal()}>
          <div className="modal-content">
            <span className="close-btn" onClick={closeModal}>
              &times;
            </span>
            <h2>Confirm Your Purchase</h2>
            <p id="nft-info">You are about to purchase: {nftType}</p>
            <div className="modal-buttons">
              <button id="confirm-purchase" onClick={confirmPurchase}>
                Confirm Purchase
              </button>
              <button className="cancel-btn" onClick={closeModal}>
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
